'    sys.htp.prn(''</span>'');',
'    --',
'    --',
'    --',
'    sys.htp.p(''</li>'');',
'    --',
'    --',
'    --',
'    if c = l_max_rows then',
'       exit;',
'    end if;',
'',
'  end loop;',
'  if c > 0 then ',
'    sys.htp.p(''</ul>'');',
'    sys.htp.p(''<p>''||c||'' results found.</p>'');',
'  else ',
'    sys.htp.p(''<p>No search results.</p>'');',
'  end if;',
'end if;',
'end;'))
,p_plug_source_type=>'NATIVE_PLSQL'
,p_plug_query_row_template=>1
