'end loop;',
'',
'for rec in dbmode loop',
'  dbmodeStr := dbmodeStr || rec.DATABASE_MODE_ID || '':'';',
'end loop;',
'',
'for rec in dbrole loop',
'  dbroleStr := dbroleStr || rec.DATABASE_ROLE_ID || '':'';',
