':P1037_DOC_ID := NULL;',
':FILE_ID := NULL;',
':P1037_SF_LINK_ID_WHEN_LOADED := :P1037_SF_LINK_ID;',
'end;'))
,p_process_when=>wwv_flow_string.join(wwv_flow_t_varchar2(
'if :P1037_CHECK_ID is not null and :P1037_LOADING_AC = 1 then',
