,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create or replace PACKAGE RCA13_GET_DATA4COLUMNS AS ',
'  function getIBSwitches(collectionId varchar2,cNo number) return varchar2;',
