'procedure monitorDiffType is',
'temp varchar2(40);',
'begin',
' for rec in ( select collection_date cDate,collection_name cName from rca13_collections_md',
'              where collection_date > sysdate - 90',
'              ) loop',
'   temp := hasDiffWithPrevRun(rec.cName,rec.cDate);',
