'rVal number := 1;',
'flag number := 0;',
'messageHeader clob := NULL;',
'messagespacecrunch clob := NULL;',
'messageFooter clob := NULL;',
'spaceflag  number := 0;',
'tsName varchar2(256);',
'freeSpace number := 999;',
'talert varchar2(50);',
'begin',
' messageHeader := ''',
' <html><head>',
' <style>',
'  table {    ',
'    font-weight: bold;',
'    border-spacing: 0;',
'    outline: medium none;',
'    font-family: Lucida Grande,Lucida Sans,Arial,sans-serif;',
