'   end if;  ',
'  exception when others then',
' trace_log(''sendnotificationsaccessException''||SQLCODE||SQLERRM); ',
'  end;',
' end loop; ',
'exception when others then',
'trace_log(''sendnotificationsuserdetailsException''||SQLCODE||SQLERRM); ',
'null;',
'end;',
