commands.script:
conn sysman/%SYSMAN_PWD%; set mark html on spool ReposClusterInfo.html select entity_guid, entity_type,entity_name, manage_status,promote_status, host_name, emd_url, display_name from sysman.em_manageable_entities where entity_type in ('oracle_database','rac_database','oracle_listener','osm_instance', 'osm_cluster','has','cluster','oracle_pdb','oracle_dbsys');
select target_name, target_type from sysman.MGMT_TARGETS_DELETE where DELETE_COMPLETE_TIME is NULL;
SELECT plugin_id, plugin_name, version, destination_name FROM sysman.gc$current_deployed_plugin WHERE content_type= 'Discovery' AND destination_type = 'Agent' and destination_name like '%EMHOSTNAME%' ORDER BY destination_name, plugin_name, version;
spool off set mark html off exit;
